import React, { useState } from "react";
import { Button } from "react-bootstrap";

export default function TextFormatter() {
  const [text, setText] = useState("");
  const [time, setTime] = useState(0);
  const [wordd, setWord] = useState(0);
  const [copyText, setCopyText] = useState("");

  const handleText = (e) => {
    var word = e.target.value;
    setText(word);
    setCopyText(e.target.value);
    const words = text.trim().split(/\s+/).length;
    setWord(words);

    const wpm = 225;
    const time = Math.ceil(words / wpm);
    setTime(time);
  };

  const changeToUpeer = () => {
    setText(text.toUpperCase());
  };

  const changeToLower = () => {
    setText(text.toLowerCase());
  };

  const capitalizeWords = () => {
    const Capitalize = text.split(" ");

    for (var i = 0; i < Capitalize.length; i++) {
      Capitalize[i] =
        Capitalize[i].charAt(0).toUpperCase() + Capitalize[i].slice(1);
    }

    const newText = Capitalize.join(" ");
    setText(newText);
  };

  const downloadFile = () => {
    const blob = new blob([text], { type: "text/plain" });
    const fileUrl = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.download = fileUrl;
    link.click();
  };

  const copyTe = () => {
    navigator.clipboard.writeText(copyText);
    alert("copied!");
  };

  return (
    <div className="App m-3">
      <p className="text-center display-5 fst-italic">Text Formatter</p>
      {/* <textarea value={text} onChange={handleText} /> */}
      <div className="form-group">
        <textarea
          value={text}
          onChange={handleText}
          className="form-control bg-light"
          rows="5"
          cols="10"
        />
      </div>
      <div className="d-flex justify-content-around mt-3">
        <p className="border-bottom border-warning">
          Character Count : {text.length}
        </p>
        <p className="border-bottom border-warning">Read Time : {time}min</p>
        <p className="border-bottom border-warning">Word count : {wordd}</p>
      </div>
      <div className="d-flex justify-content-center">
        <Button variant="primary" className="me-3" onClick={changeToUpeer}>
          Upper case
        </Button>
        <Button variant="primary" className="me-3" onClick={changeToLower}>
          Lowewr case
        </Button>
        <Button variant="primary" className="me-3" onClick={capitalizeWords}>
          Capitalize words
        </Button>
        <Button variant="primary" className="me-3" onClick={copyTe}>
          Copy text
        </Button>
        <Button variant="primary" className="me-3" onClick={downloadFile}>
          Download File
        </Button>
      </div>
      <div className="d-flex justify-content-end w-100% h-50%">
        <img src="https://www.shutterstock.com/image-vector/business-intelligence-analytics-planning-abstract-600w-1869139441.jpg" />
      </div>
    </div>
  );
}
